mapper.select:

select 

from grades
where studentId = #{studentid,jdbcType=VARCHAR}
  and courseId = #{courseid,jdbcType=VARCHAR}
  and gradeId = #{gradeid,jdbcType=VARCHAR}

mapper.delete:

delete from grades
where studentId = #{studentid,jdbcType=VARCHAR}
  and courseId = #{courseid,jdbcType=VARCHAR}
  and gradeId = #{gradeid,jdbcType=VARCHAR}

mapper.update:

update grades

  
    score = #{score,jdbcType=DOUBLE},
  
  
    enrollmentStatus = #{enrollmentstatus,jdbcType=SMALLINT},
  
  
    gpa = #{gpa,jdbcType=DOUBLE},
  
  
    assessmentMethod = #{assessmentmethod,jdbcType=VARCHAR},
  
  
    remarks = #{remarks,jdbcType=VARCHAR},
  

where studentId = #{studentid,jdbcType=VARCHAR}
  and courseId = #{courseid,jdbcType=VARCHAR}
  and gradeId = #{gradeid,jdbcType=VARCHAR}


update grades
set score = #{score,jdbcType=DOUBLE},
  enrollmentStatus = #{enrollmentstatus,jdbcType=SMALLINT},
  gpa = #{gpa,jdbcType=DOUBLE},
  assessmentMethod = #{assessmentmethod,jdbcType=VARCHAR},
  remarks = #{remarks,jdbcType=VARCHAR}
where studentId = #{studentid,jdbcType=VARCHAR}
  and courseId = #{courseid,jdbcType=VARCHAR}
  and gradeId = #{gradeid,jdbcType=VARCHAR}
